(function() {
	var account;
	
	account = (function() {
		
		function account(client) {
			this.client = client;
		}

		account.prototype.projects = function(archived, cb) {
			var getArchived
			if (typeof archived === 'function') {
				cb = archived;
				getArchived = false;
			} else {
				getArchived = archived;
			}
			return this.client.get("/projects", function(err, s, b) {
				if (err) {
					return cb(err);
				}
				if (s !== 200) {
					return cb(new Error('API Error'));
				} else {
					return cb(null, b);
				}
			});
		};

		return account;

	})();
	
	module.exports = account;
	
}).call(this);
